INSERT INTO `lt_article` VALUES ('32', '公司简介', '公司简介', '公司简介', '刘', '\\uploads/20190309\\b68d986288f9c5a1b20a0d58703075d6.jpg', '<p style=\"white-space: normal; text-align: center;\"><img src=\"http://www.luntan.com/ueditor/php/upload/image/20190309/1552119384.jpg\" title=\"1552119384.jpg\" alt=\"关于我们.jpg\"/></p><p style=\"white-space: normal;\">总公司：广东泰安模塑科技股份有限公司&nbsp;</p><p style=\"white-space: normal;\">广州分公司：广州金泰安汽车饰件有限公司&nbsp;</p><p style=\"white-space: normal;\">香港分公司：香港金泰安模塑科技有限公司&nbsp;</p><p style=\"white-space: normal;\">总公司地址：广东省中山市火炬开发区集中新建区A28栋A1厂房&nbsp;</p><p style=\"white-space: normal;\">广州分公司地址：广州市番禺区化龙镇国贸大道南43号厂房2&nbsp;</p><p style=\"white-space: normal;\">总资产：RMB 2.47亿&nbsp;</p><p style=\"white-space: normal;\">职员人数：640人&nbsp;</p><p style=\"white-space: normal;\">总工厂面积：40000㎡&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;</p><p style=\"white-space: normal;\">&nbsp; &nbsp; &nbsp; 广东泰安模塑科技股份有限公司于二00九年五月成立，主要业务是： 从事汽车内外饰零部件设</p><p style=\"white-space: normal;\">计与制造，泰安致力于成为专业的汽车零部件模块供应商；公司拥有非常完善的多种生产工艺，可满<br/></p><p style=\"white-space: normal;\">足各种汽车内外饰零部件的生产需求，泰安公司为客户提供全方位的生产工艺与技术服务如下：设计<br/></p><p style=\"white-space: normal;\">与分析零部件产品图纸—设计与制造零部件产品注塑成型模具、检具、工装治具—注塑成型加成为零<br/></p><p style=\"white-space: normal;\">件—塑料零件的表面处理—零件组装 其中塑料零件表面处理，泰安有多种表面处理工艺；如下：<br/></p><p style=\"white-space: normal;\">①塑料零件表面PU发泡&nbsp;</p><p style=\"white-space: normal;\">②塑料零件表面皮革包覆&nbsp;</p><p style=\"white-space: normal;\">③塑料零件表面上皮革包覆的阴模成型、真空成型、热压成型&nbsp;</p><p style=\"white-space: normal;\">④塑料零件表面高光涂装&nbsp;</p><p style=\"white-space: normal;\">⑤塑料零件表面移印、丝印、烫印&nbsp;</p><p style=\"white-space: normal;\">⑥零件超声波组装&nbsp;</p><p style=\"white-space: normal;\">&nbsp; &nbsp; &nbsp; 公司全面推行ISO/TS16949国际质量标准管理体系和ISO/TS14001环境质量管理体系，公司坚持</p><p style=\"white-space: normal;\">以客服为导向，品质为上的理念来促进公司的发展与进步，并提升公司的竞争力。<br/></p><p><br/></p>', '8', '0', '1', '1552120573', '17');
